INSERT INTO `recordings` (`displayname`,`filename`,`description`,`fcode`,`fcode_pass`) values ('demo-echotest','demo-echotest','You\ are\ about\ to\ enter\ an\ echo\ test.\ \ In\ this\ mode\ everything\ you\ say\ will\ be\ repeated\ back\ to\ you\ just\ as\ soon\ as\ it\ is\ received.\ \ The\ purpose\ of\ this\ test\ is\ to\ give\ you\ an\ audible\ sense\ of\ the\ latency\ between\ you\ and\ the\ machine\ that\ is\ running\ the\ echo\ test\ application.\ \ You\ may\ end\ the\ test\ by\ hanging\ up\ or\ by\ pressing\ the\ pound\ key.',0,'en');
