INSERT INTO `recordings` (`displayname`,`filename`,`description`,`fcode`,`fcode_pass`) values ('demo-instruct','demo-instruct','If\ you\ would\ like\ to\ learn\ more\ technical\ information\ about\ Asterisk\ dial\ 2\ now.\ \ If\ you\'d\ like\ to\ test\ out\ the\ voice\ over\ IP\ capabilities\ of\ Asterisk\ you\ can\ dial\ 500\ to\ attempt\ an\ Inter-Asterisk\ Exchange\ or\ IAX\ connection\ to\ a\ demonstration\ server\ at\ Digium.\ \ In\ order\ for\ this\ test\ to\ work\ you\ will\ need\ to\ be\ connected\ to\ the\ Internet\ and\ have\ at\ least\ a\ 28.8\ kilobit\ modem.\ \ To\ execute\ an\ echo\ test\ dial\ 600.\ \ This\ test\ is\ most\ useful\ when\ you\ have\ connected\ to\ this\ Asterisk\ server\ from\ a\ remote\ location.\ \ The\ sample\ configuration\ also\ has\ a\ single\ user\ with\ extension\ 1234\ and\ password\ 4242.\ \ That\ user\ is\ configured\ to\ ring\ the\ console\ when\ their\ extension\ is\ dialed.\ \ If\ you\ dial\ 1234\ you\ can\ try\ to\ ring\ the\ console.\ \ If\ the\ console\ is\ busy\ or\ unavailable\ you\ will\ be\ given\ the\ option\ to\ leave\ voicemail.\ \ To\ check\ voice\ mail\ for\ the\ user\ dial\ extension\ 8500\ to\ enter\ the\ voicemail\ system.\ \ Finally\,\ \ you\ can\ press\ the\ pound\ key\ to\ disconnect\ from\ the\ PBX.',0,'en');
INSERT INTO `recordings` (`displayname`,`filename`,`description`,`fcode`,`fcode_pass`) values ('demo-moreinfo','demo-moreinfo','Asterisk\ is\ an\ Open\ Source\ full-featured\ PBX\ and\ IVR\ platform\ that\ runs\ on\ the\ linux\ operating\ system.\ \ For\ more\ information\,\ visit\ www.asterisk.org.',0,'en');
